import React, { useState } from "react";
import {
  Problems,
  ProblemBg,
  ProblemImg,
  ProblemTitle,
} from "./ProblemElements";
import img from "../../images/failure.jpg";
import img2 from "../../images/failure2.jpg";

function Problem() {
  const [size, setSize] = useState(24);
  const [style, setStyle] = useState({});

  setTimeout(() => {
    setStyle(size * 2);

    const newStyle = {
      opacity: 1,
      width: `${size}%`,
    };

    setStyle(newStyle);
  }, 1);

  return (
    <>
      <Problems id="problem">
        <ProblemTitle size={size}>Ne, vazduh nije zagadjen.</ProblemTitle>
        <ProblemBg>
          <ProblemImg src={img2} />
        </ProblemBg>
      </Problems>
    </>
  );
}

export default Problem;
