import React, { useState } from "react";
import "./LoadingBar.css";

function LoadingBar({ done, title }) {
  const [style, setStyle] = useState({});

  setTimeout(() => {
    const newStyle = {
      opacity: 1,
      width: `${done}%`,
    };

    setStyle(newStyle);
  }, 0);

  return (
    <div className="progress">
      <div className="progress-done" style={style}>
        {done}
      </div>
      <div className="progress__first">0</div>
      <div className="progress__last">4000</div>
    </div>
  );
}

export default LoadingBar;
