import React, { useEffect, useState } from "react";
import LoadingBar from "../LoadingBar/LoadingBar";
import {
  Isprobaj,
  IsprobajResults,
  IsprobajWrapper,
  LoadingBarTitle,
  IsprobajInput,
  IsprobajResultsContainer,
  InputTemperatura,
  Select,
  Button,
  IsprobajInputContainer,
  IsprobajInputNaslov,
  IsprobajWeather,
  WeatherImg,
  WeatherTemperature,
  WeatherInfo,
  WeatherContainerOne,
  WeatherInfoContainer,
  WeatherContainerTwo,
  WeatherWrapper,
} from "./IsprobajElements";

function Test() {
  const [barPercentage, setbarPercentage] = useState(2000);
  const [temperature, setTemperature] = useState("");
  const [weatherTemperature, setWeatherTemperature] = useState("");
  const [pritisak, setPritisak] = useState("");
  const [vlaznost, setVlaznost] = useState("");
  const [brzinaVetra, setBrzinaVetra] = useState("");
  const [oblacnost, setOblacnost] = useState("");
  const [dan, setDan] = useState("");
  const [mesec, setMesec] = useState("");

  const [loading, setLoading] = useState(false);
  const [weatherImg, setWeatherImg] = useState("");
  const [precipitation, setPrecipitation] = useState("");
  const [visibility, setVisibility] = useState("");
  const [preassure, setPreassure] = useState("");
  const [country, setCountry] = useState("");
  const [city, setCity] = useState("");
  const [time, setTime] = useState("");
  const [weatherDesc, setWeatherDesc] = useState("");

  const [data, setData] = useState();

  const URL =
    "http://api.weatherstack.com/current?access_key=c2c58ca6c51f7422ed48bd46ba4baf7f&query=Belgrade";
  const PyURL = "http://24.135.203.92:8080/hackathon/api/airquality/quality";

  const temperatureChange = (e) => {
    e.preventDefault();
    setTemperature(e.target.value);
  };

  const vlaznostChange = (e) => {
    e.preventDefault();
    setVlaznost(e.target.value);
  };

  const pritisakChange = (e) => {
    e.preventDefault();
    setPritisak(e.target.value);
  };

  const brzinaVetraChange = (e) => {
    e.preventDefault();
    setBrzinaVetra(e.target.value);
  };

  const oblacnostChange = (e) => {
    e.preventDefault();
    setOblacnost(e.target.value);
  };

  const danChange = (e) => {
    e.preventDefault();
    setDan(e.target.value);
  };

  const mesecChange = (e) => {
    e.preventDefault();
    setMesec(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(!loading);
    console.log(
      temperature,
      vlaznost,
      pritisak,
      brzinaVetra,
      oblacnost,
      dan,
      mesec
    );
  };

  useEffect(() => {
    async function postData() {
      try {
        let res = await fetch(PyURL, {
          method: "post",
          mode: "no-cors",
          headers: {
            Accept: "application/json",
            "Content-type": "application/json",
          },
          body: JSON.stringify({
            vreme: 21,
            temperatura: 11,
            vlaznost: 82,
            pritisak: 1000,
            brzina: 10,
            oblacnost: 82,
            "dan u nedelji": 3,
            mesec: 10,
          }),
        });
      } catch (e) {
        console.log(e);
      }
    }
  }, [loading]);

  useEffect(() => {
    const requestOption = {
      method: "GET",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        temperature,
        vlaznost,
        pritisak,
        brzinaVetra,
        oblacnost,
        dan,
        mesec,
      }),
    };

    fetch(URL)
      .then((response) => response.json())
      .then((data) => {
        setWeatherImg(data.current.weather_icons[0]);
        setWeatherTemperature(data.current.temperature);
        setPrecipitation(data.current.precip);
        setVisibility(data.current.visibility);
        setPreassure(data.current.pressure);
        setCountry(data.location.country);
        setCity(data.location.name);
        setTime(data.current.observation_time);
        setWeatherDesc(data.current.weather_descriptions[0]);
      })
      .catch((e) => console.log(e));

    // fetch(PyURL, {
    //   method: "post",

    //   body: JSON.stringify({
    //     temperature: temperature,
    //     vlaznost,
    //     pritisak,
