import React, { useEffect, useState } from "react";
import LoadingBar from "../LoadingBar/LoadingBar";
import {
  Isprobaj,
  IsprobajResults,
  IsprobajWrapper,
  LoadingBarTitle,
  IsprobajInput,
  IsprobajResultsContainer,
  InputTemperatura,
  Select,
  Button,
  IsprobajInputContainer,
  IsprobajInputNaslov,
  IsprobajWeather,
  WeatherImg,
  WeatherTemperature,
  WeatherInfo,
} from "./IsprobajElements";

function Test() {
  const [barPercentage, setbarPercentage] = useState(2000);
  const [temperature, setTemperature] = useState("");
  const [loading, setLoading] = useState(false);
  const [weatherImg, setWeatherImg] = useState("");
  const [precipitation, setPrecipitation] = useState("");
  const [visibility, setVisibility] = useState("");
  const [preassure, setPreassure] = useState("");

  const URL =
    "http://api.weatherstack.com/current?access_key=c2c58ca6c51f7422ed48bd46ba4baf7f&query=Belgrade";

  const temperatureChange = (e) => {
    e.preventDefault();
    setTemperature(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setLoading(!loading);
  };

  useEffect(() => {
    fetch(URL)
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setWeatherImg(data.current.weather_icons[0]);
        setTemperature(data.current.temperature);
        setPrecipitation(data.current.precip);
        setVisibility(data.current.visibility);
        setPreassure(data.current.pressure);
      })
      .catch((e) => console.log(e));
    console.log("load");
  }, [loading]);

  return (
    <>
      <Isprobaj id="isprobaj">
        <IsprobajInputContainer>
          <IsprobajInput onSubmit={handleSubmit}>
            <IsprobajInputNaslov>Unesi podatke</IsprobajInputNaslov>
            <InputTemperatura
              value={temperature}
              onChange={temperatureChange}
              placeholder="Temperatura"
              type="text"
              required
            ></InputTemperatura>
            <InputTemperatura
              onChange={temperatureChange}
              value={temperature}
              placeholder="Temperatura"
              type="text"
              required
            ></InputTemperatura>
            <InputTemperatura
              onChange={temperatureChange}
              value={temperature}
              placeholder="Temperatura"
              type="text"
              required
            ></InputTemperatura>
            <Select required>
              <option value="Wind" hidden>
                Type
              </option>
              <option value="Wind1">Wind1</option>
              <option value="Wind1">Wind1</option>
              <option value="Wind1">Wind1</option>
              <option value="Wind1">Wind1</option>
            </Select>
            <Button type="submit">Submit</Button>
          </IsprobajInput>
        </IsprobajInputContainer>
        <IsprobajWeather>
          <WeatherImg src={weatherImg}></WeatherImg>
          <WeatherTemperature>{temperature}°</WeatherTemperature>
          <WeatherInfo>{precipitation}%</WeatherInfo>
          <WeatherInfo>{visibility}</WeatherInfo>
          <WeatherInfo>{preassure}</WeatherInfo>
        </IsprobajWeather>
        <IsprobajResultsContainer id="test1">
          <IsprobajResults>
            <IsprobajWrapper>
              <LoadingBarTitle>Temperature</LoadingBarTitle>
              <LoadingBar
                min_progress="0"
                max_progress="6000"
                title="Temperature"
                done={barPercentage}
              />
            </IsprobajWrapper>
            <IsprobajWrapper>
              <LoadingBarTitle>Temperature</LoadingBarTitle>
              <LoadingBar
                min_progress="0"
                max_progress="6000"
                title="Temperature"
                done={barPercentage}
              />
            </IsprobajWrapper>
            <IsprobajWrapper>
              <LoadingBarTitle>Temperature</LoadingBarTitle>
              <LoadingBar
                min_progress="0"
                max_progress="6000"
                title="Temperature"
                done={barPercentage}
              />
            </IsprobajWrapper>
            <IsprobajWrapper>
              <LoadingBarTitle>Temperature</LoadingBarTitle>
              <LoadingBar
                min_progress="0"
                max_progress="6000"
                title="Temperature"
                done={barPercentage}
              />
            </IsprobajWrapper>
            <IsprobajWrapper>
              <LoadingBarTitle>Temperature</LoadingBarTitle>
              <LoadingBar
                min_progress="0"
                max_progress="6000"
                title="Temperature"
                done={barPercentage}
              />
            </IsprobajWrapper>
          </IsprobajResults>
        </IsprobajResultsContainer>
      </Isprobaj>
    </>
  );
}

export default Test;
