import styled from "styled-components";

export const Isprobaj = styled.div`
  background: #0c0c0c;
  display: flex;
  justify-content: center;
  align-items: center;
  padding: 0 30px;
  height: 100vh;
  position: relative;
  z-index: 1;
  background: #010606;

  @media screen and (max-width: 768px) {
    height: 150vh;
    flex-direction: column;
  }
`;

export const IsprobajResults = styled.div`
  justify-content: center;
  height: 100%;
  display: grid;
  margin-left: 20%;
  grid-template-columns: 1fr;
  grid-template-rows: repeat(5, 150px);
  text-align: center;

  @media screen and (max-width: 768px) {
    width: 100%;
    margin-left: 15px;
    padding-top: 30%;
    grid-template-rows: repeat(5, 120px);
  }

  @media screen and (max-width: 480px) {
    width: 100%;
    margin-left: 0;
    grid-template-rows: repeat(5, 120px);
  }
`;

export const IsprobajResultsContainer = styled.div`
  flex: 0 1 50%;
  max-width: 50%;

  @media screen and (max-width: 480px) {
    flex: none;
  }
`;

export const IsprobajInputContainer = styled.div`
  flex: 0 1 50%;
  max-width: 50%;
  display: flex;
  justify-content: center;
`;

export const IsprobajInput = styled.form`
  background: #36454f;
  justify-content: center;
  align-items: center;
  height: 100%;
  padding: 10px 20px;
  width: 40%;
  display: grid;
  border-radius: 10px;
  grid-template-columns: 250px;
  grid-template-rows: repeat(4, 80px);
  text-align: center;

  @media screen and (max-width: 768px) {
    flex: none;
  }
`;

export const IsprobajWrapper = styled.div``;

export const LoadingBarTitle = styled.h1`
  width: 300px;
  color: #fff;
`;

export const InputTemperatura = styled.input`
  padding: 0.5em;
  margin: 0.5em;
  color: black;
  background: #fff;
  border: none;
  border-radius: 5px;
`;

export const Select = styled.select`
  padding: 0.5em;
  margin: 0.5em;
  height: 50px;
  background: white;
  color: gray;
  font-size: 14px;
  border: none;

  option {
    color: black;
    background: white;
    display: flex;
    white-space: pre;
    min-height: 20px;
    padding: 0px 2px 1px;
  }
`;

export const Button = styled.button`
  padding: 0.5em;
  margin: 0.5em;
  border-radius: 10px;
  background: red;
  white-space: nowrap;
  color: ${({ dark }) => (dark ? "#fff" : "#fff")};
  font-size: ${({ fontBig }) => (fontBig ? "20px" : "16px;")};
  outline: none;
  border: none;
  cursor: pointer;
  display: flex;
  justify-content: center;
  align-items: center;
  transition: all 0.2s ease-in-out;

  &:hover {
    color: #000;
    transition: all 0.2s ease-in-out;
    background: #fff;
  }
`;
