import styled from "styled-components";

export const ResenjeContainer = styled.div`
  background: #000;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  padding: 0 30px;
  height: 100vh;
  position: relative;
  z-index: 1;
  /* background: #010606; */
  background: #000;
  text-align: left;
`;

export const ResenjeTitle = styled.h1`
  color: #fff;
`;

export const ResnjeContent = styled.div`
  justify-content: center;
  align-items: center;
  height: 100%;
  padding: 10px 20px;
  width: 60%;
  display: grid;
  border-radius: 10px;
  grid-template-columns: 250px;
  grid-template-rows: repeat(3, 80px);
  text-align: center;
`;
