  background: #010606;

  @media screen and (max-width: 768px) {
    height: 200vh;
  }
`;

export const IsprobajResults = styled.div`
  height: 100%;
  display: grid;
  margin-left: 20%;
  grid-template-columns: 1fr;
  grid-template-rows: repeat(5, 150px);
  text-align: center;

  @media screen and (max-width: 768px) {
    width: 100%;
    margin-left: 15px;
    grid-template-rows: repeat(5, 120px);
